import React, { useState } from "react";
import "./Countries.css";

const USERS = [
  { id: 1, name: "Andy" },
  { id: 2, name: "Bob" },
  { id: 3, name: "Tom Hulk" },
  { id: 4, name: "Tom Hank" },
  { id: 5, name: "Audra" },
  { id: 6, name: "Anna" },
  { id: 7, name: "Tom" },
  { id: 8, name: "Tom Riddle" },
  { id: 9, name: "Bolo" },
];

const Countries = props => {
  const countries = store.getState().countries.countries;
  const region = countries.filter(country => country.region === "Asia");
  console.log(region);
  // the value of the search field
  const [name, setName] = useState("");

  // the search result
  const [foundUsers, setFoundUsers] = useState(USERS);

  const filter = (e: React.FormEvent<HTMLInputElement>) => {
    const keyword = (e.target as HTMLInputElement).value;

    if (keyword !== "") {
      const results = USERS.filter(user => {
        return user.name.toLowerCase().startsWith(keyword.toLowerCase());
        // Use the toLowerCase() method to make it case-insensitive
      });
      setFoundUsers(results);
    } else {
      setFoundUsers(USERS);
      // If the text field is empty, show all users
    }

    setName(keyword);
  };

  return (
    <section>
      <div className="container">
        <input
          type="search"
          value={name}
          onChange={filter}
          className="input"
          placeholder="Searh for a country"
        />

        <div className="user-list"></div>
      </div>
    </section>
  );
};

