import { useState, useEffect } from "react";

export type TApiResponse = {
  data: [];
  // error: any;
  // loading: Boolean;
};

export const useApiGet = (url: string): TApiResponse => {
  const [data, setData] = useState<any>([]);
  // const [error, setError] = useState<any>();
  // const [loading, setLoading] = useState<boolean>(false);

  const fetchtData = async () => {
    try {
      //setLoading(true);
      const response = await fetch(url);
      const data = await response.json();
      if (data) {
        const countryData = data.map((info: any) => {
          const {
            capital,
            flags,
            name,
            region,
            population,
            launguages,
            borders,
          } = info;

          return {
            capital,
            flags,
            name,
            region,
            population,
            launguages,
            borders,
            favorite: false,
          };
        });
        setData(countryData);
      }
    } catch (error) {
      // setError(error);
    }
    //setLoading(false);
  };

  useEffect(() => {
    fetchtData();
  }, []);

  return { data };
};
