import React from "react";

import { useDispatch } from "react-redux";
import { getCountries } from "./store/countrySlice";
import { store } from "./store/store";
import { useFetch } from "./hooks/useFetch";
import "./App.css";

const App: React.FC = () => {
  const { data, loading, error } = useFetch(
    "https://restcountries.com/v2/all/"
  );

  const dispatch = useDispatch();

  dispatch(getCountries(data));

  const countries = store.getState().countries.countries;
  const region = countries.filter(country => country.region === "Asia");
  console.log(region);

  if (error) {
    console.log(error);
  }

  return (
    <div className="content-container">
      <div className="row">
        <div className="left-panel box">
          <Sidebar title="Favorites" width={300} height="100vh">
            <ul className="favs-ul">
              <li className="favs-li">
                <span>nav Item</span>
                <button className="songs-button">x</button>
              </li>
            </ul>
          </Sidebar>
        </div>

        <div className="right-panel box">
          <Header />
          <Countries />
        </div>
      </div>
    </div>
  );
};

export default App;

