import { useState, useEffect } from "react";

import { store } from "../store/store";
import Country from "./Country";

import "../css/Countries.css";

interface Props {
  region: string;
}

const Countries: React.FC<Props> = props => {
  const countries = store.getState().countries.countries;

  const [name, setName] = useState("");

  const filter = (e: React.FormEvent<HTMLInputElement>) => {
    const keyword = (e.target as HTMLInputElement).value;
    setName(keyword);
  };

  return (
    <section>
      <div className="container">
        <input
          type="search"
          value={name}
          onChange={filter}
          className="input"
          placeholder="Search for a country"
        />

        <div className=""></div>
      </div>

      {
        <div>
          {countries && countries.length > 0 ? (
            countries
              .filter(country => country.region === props.region)
              .filter(country => {
                return (
                  name === "" ||
                  country.name.toLowerCase().startsWith(name.toLowerCase())
                );
              })
              .map(country => (
                <li key={country.countryCode} className="countries-section">
                  <Country country={country} fav="add" />
                </li>
              ))
          ) : (
            <h1>No countries found!</h1>
          )}
          setName("");
        </div>
      }
    </section>
  );
};

export default Countries;
