import { createSlice } from "@reduxjs/toolkit";
import { ICountry, FavoriteState, DispatchType } from "../types";

const initialState: FavoriteState = {
  favorites: [],
};

export const favoriteSlice = createSlice({
  name: "favorites",
  initialState,
  reducers: {
    addFav: (state, action) => {
      const newFav = action.payload;
      const existingFav = state.favorites.find(
        fav => fav.countryCode === newFav.countryCode
      );

      //   state.favorites = action.payload;
    },
    removeFav: (state, action) => {
      const id = action.payload;
      state.favorites = state.favorites.filter(
        favorite => favorite.countryCode !== id
      );
    },
  },
});

export const { addFav, removeFav } = favoriteSlice.actions;

export default favoriteSlice.reducer;
