import React, { useState } from "react";
import "./Country.css";

import { ICountry } from "../types";

interface Props {
  country: ICountry;
}

const Country: React.FC<Props> = props => {
  const [showCountry, setShowCountry] = useState(false);
  const { name, flags, population, region, capital, borders, languages } =
    props.country;

  const toggleShow = () => {
    setShowCountry(prevShowCountry => !prevShowCountry);
  };
  return (
    <div className="country">
      <div className="country-info">
        <img src={flags.png} alt={name} />
        <h3>{name}</h3>

        <button onClick={toggleShow}>{!showCountry ? "Show" : "Hide"}</button>
        {showCountry && (
          <div>
            <p>
              Capital: <span>{capital}</span>{" "}
            </p>

            <p>
              Population: <span>{population}</span>{" "}
            </p>
            <p>
              Region: <span>{region}</span>{" "}
            </p>

            <div className="country-borders">
              <p>Languages:</p>
              <ul>
                {languages &&
                  languages.map(language => (
                    <li key={language.name}>{language.name}</li>
                  ))}
              </ul>
            </div>

            <div className="country-borders">
              <p>Borders:</p>
              <ul>
                {borders &&
                  borders.map(border => <li key={border}>{border}</li>)}
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Country;

