import { useState, useEffect } from "react";
import { store } from "../store/store";
import Country from "./Country";
import "../css/Countries.css";

interface Props {
  region: string;
}

const Countries: React.FC<Props> = props => {
  const countries = store.getState().countries.countries;
  const [isInitialRender, setIsInitialRender] = useState(true);

  const countryList = countries.filter(
    country => country.region === props.region
  );

  const [name, setName] = useState("");
  const [foundCountries, setFoundCountries] = useState(countryList);

  useEffect(() => {
    if (isInitialRender) {
      setIsInitialRender(false);
      setFoundCountries(countryList);
    }
  }, [countryList, isInitialRender]);

  const filter = (e: React.FormEvent<HTMLInputElement>) => {
    const keyword = (e.target as HTMLInputElement).value;

    if (keyword !== "") {
      const results = countryList.filter(country => {
        return country.name.toLowerCase().startsWith(keyword.toLowerCase());
      });
      setFoundCountries(results);
    } else {
      setFoundCountries(countryList);
    }

    setName(keyword);
  };

  return (
    <section>
      <div className="container">
        <input
          type="search"
          value={name}
          onChange={filter}
          className="input"
          placeholder="Search for a country"
        />

        <div className=""></div>
      </div>

      {
        <div>
          {foundCountries && foundCountries.length > 0 ? (
            foundCountries.map(country => (
              <li key={country.countryCode} className="countries-section">
                <Country country={country} fav="add" />
              </li>
            ))
          ) : (
            <h1>No countries found!</h1>
          )}
        </div>
      }
    </section>
  );
};

export default Countries;
