import React from "react";
import { Plane } from "react-loader-spinner";
import { useDispatch } from "react-redux";

import { useFetch } from "./hooks/useFetch";

import { getCountries } from "./store/countrySlice";
import { copyFav } from "./store/favoriteSlice";

import Sidebar from "./components/Sidebar";
import Favorites from "./components/Favorites";
import Header from "./components/Header";

import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import "./App.css";

const getStorage = JSON.parse(localStorage.getItem("favorites"));

getStorage(copyFav(JSON.parse(localStorage.getItem("favorites"))));

let storage = JSON.parse(localStorage.getItem("favorites"));

const App: React.FC = () => {
  const favs = useDispatch();
  dispatch(addFav(storage));

  const { data, loading, error } = useFetch(
    "https://restcountries.com/v2/all/"
  );

  const dispatch = useDispatch();
  dispatch(getCountries(data));

  return (
    <>
      <div className="content-container">
        <div className="row">
          <div className="left-panel box">
            <Sidebar width={400} height="100vh">
              <Favorites />
            </Sidebar>
          </div>

          <div className="right-panel box">
            <Header />
            <section className="spinner">{loading && <Plane />}</section>
          </div>
        </div>
      </div>
    </>
  );
};

export default App;

