import React from "react";
import { useState } from "react";

import { store } from "../store/store";
import Country from "./Country";
import { ICountry } from "../types";

import "../css/Countries.css";

interface Props {
  countries: ICountry[];
  fav: string;
  region: string;
}

const FilteredCountries: React.FC<Props> = props => {
  const countries = props.countries;
  const fav = props.fav;

  const [name, setName] = useState("");

  const filter = (e: React.FormEvent<HTMLInputElement>) => {
    const keyword = (e.target as HTMLInputElement).value;
    setName(keyword);
  };

  return (
    <section>
      <div className="container">
        <input
          type="search"
          value={name}
          onChange={filter}
          className="input"
          placeholder="Search for a country"
        />

        <div className=""></div>
      </div>

      {
        <div>
          {countries && countries.length > 0 ? (
            countries

              .filter(country => {
                return (
                  name === "" ||
                  country.name.toLowerCase().startsWith(name.toLowerCase())
                );
              })
              .map(country => (
                <li key={country.countryCode} className="countries-section">
                  <Country country={country} fav={fav} />
                </li>
              ))
          ) : (
            <h1>No countries found!</h1>
          )}
        </div>
      }
    </section>
  );
};

export default FilteredCountries;
