import { useState, useRef, useImperativeHandle } from "react";

import Country from "./Country";
import { ICountry } from "../types";

import "../css/Countries.css";
import "../css/Favorites.css";

interface Props {
  countries: ICountry[];
  fav: string;
  placeholder: string;
  openedMenu?: boolean;
}

const FilterCountries: React.FC<Props> = props => {
  const { countries, fav, placeholder, openedMenu } = props;

  const [name, setName] = useState("");

  const filter = (e: React.FormEvent<HTMLInputElement>) => {
    const keyword = (e.target as HTMLInputElement).value;
    setName(keyword);
  };

  // const searchRef = useRef();
  // if (openedMenu && searchRef.current) {
  //   searchRef.current.value = "";
  // }

  const searchRef = useRef();
  useImperativeHandle(ref, () => ({
    changeValue: newValue => {
      searchRef.current.value = "";
    },
  }));

  const divclass = fav === "remove" ? "favs-section" : "";

  return (
    <section>
      <div className="container">
        <input
          type="search"
          value={name}
          onChange={filter}
          className="input"
          placeholder={placeholder}
          ref={searchRef}
        />

        <div className={divclass}></div>
      </div>

      {
        <div>
          {countries && countries.length > 0 ? (
            countries
              .filter(country => {
                return (
                  name === "" ||
                  country.name.toLowerCase().startsWith(name.toLowerCase())
                );
              })
              .map(country => (
                <li key={country.countryCode} className="countries-section">
                  <Country country={country} fav={fav} />
                </li>
              ))
          ) : (
            <h1>No countries found!</h1>
          )}
        </div>
      }
    </section>
  );
};

export default FilterCountries;
