import React from "react";
import { Plane } from "react-loader-spinner";
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";

import { useDispatch } from "react-redux";

import { getCountries } from "./store/countrySlice";
import { store } from "./store/store";
import { useFetch } from "./hooks/useFetch";

import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Country from "./components/Country";

import "./App.css";
import "./css/Country.css";
import "./css/Sidebar.css";

import favoriteSlice from "./store/favoriteSlice";

// onClick={() => dispatch(increment())}

const App: React.FC = () => {
  const { data, loading, error } = useFetch(
    "https://restcountries.com/v2/all/"
  );

  const dispatch = useDispatch();
  dispatch(getCountries(data));

  const favs = store.getState().favorites.favorites;
  console.log(favs);

  // if (error) {
  //   console.log(error);
  // }

  return (
    <>
      <div className="content-container">
        <div className="row">
          <div className="left-panel box">
            <Sidebar title="Favorites" width={300} height="100vh">
              {favs.map(country => (
                <li key={country.countryCode} className="countries-section">
                  <Country country={country} />
                </li>
              ))}
            </Sidebar>
          </div>

          <div className="right-panel box">
            <Header />
            <section className="spinner">{loading && <Plane />}</section>
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
