import React, { useState } from "react";
import Select from "react-select";

import Countries from "./Countries";

const options = [
  { value: "africa", label: "Africa" },
  { value: "americas", label: "Americas" },
  { value: "asia", label: "Asia" },
  { value: "europe", label: "Europe" },
  { value: "oceania", label: "Oceania" },
];

const Header = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  return (
    <>
      <section>
        <select value={selectedOption}>
          <option value="Africa">Apple</option>
          <option value="Americas">Banana</option>
          <option value="Asia">Cranberry</option>
          <option value="Europe">Apple</option>
          <option value="Oceania">Banana</option>
        </select>
        <Select
          placeholder={"Select a region ..."}
          defaultMenuIsOpen={true}
          options={options}
          onChange={setSelectedOption}
        />
      </section>

      {!selectedOption && (
        <section>
          <img
            src={process.env.PUBLIC_URL + "images/regions.png"}
            alt="regions"
            width="100%"
          />
        </section>
      )}
      {selectedOption && <Countries region={selectedOption} />}
    </>
  );
};

export default Header;

