import { useSelector } from "../store/store";
import { store } from "../store/store";
import Country from "../components/Country";
import "../css/Sidebar.css";

const Favorites = () => {
  const favs = useSelector(state => state.favorites.favorites);

  return (
    <div>
      <FilteredCountries countries={store.getState().favorites.favorites} />

      {favs.map(country => (
        <div className="favs-section">
          <li key={country.countryCode} className="countries-section">
            <Country country={country} fav="remove" />
          </li>
        </div>
      ))}
    </div>
  );
};

export default Favorites;
