import { useState, useEffect, useCallback } from "react";

export type TResponse = {
  countries: [];
  loading: Boolean;
};

export const fectch = (url: string): TResponse => {
  const [countries, setCountries] = useState<any>([]);

  const [loading, setLoading] = useState<boolean>(false);

  const useHttp = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch(url);
      const data = await response.json();
      if (data) {
        const countryData = data.map((info: any) => {
          const {
            capital,
            flags,
            name,
            region,
            population,
            launguages,
            borders,
          } = info;

          return {
            capital,
            flags,
            name,
            region,
            population,
            launguages,
            borders,
            favorite: false,
          };
        });
        setCountries(countryData);
      } else {
        setCountries([]);
      }
      setLoading(false);
    } catch (error) {
      setLoading(false);
      console.log(error);
    }
  }, []);

  useEffect(() => {
    useHttp();
  }, [useHttp]);

  // return { status, statusText, data, error, loading };
  return { countries, loading };
};
